import React from 'react';
import ReactDOM from 'react-dom';

const mapStyles = {
  map: {
    position: 'absolute',
    width: '100%',
    height: '100%'
  }
};

export class Map extends React.Component {
  constructor(props) {
    super(props);

    const { lat, lng } = this.props.initialCenter;

    const directionsService = new this.props.google.maps.DirectionsService();
    const directionsDisplay = new this.props.google.maps.DirectionsRenderer();
    const searchBox = new this.props.google.maps.places.SearchBox();

    this.state = {
      currentLocation: {
        lat: lat,
        lng: lng
      },
      directionsService : directionsService,
      directionsDisplay : directionsDisplay,
      searchBox : searchBox,
    };
  }

  componentDidMount() {
    if (this.props.centerAroundCurrentLocation) {
      if (navigator && navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(pos => {
          const coords = pos.coords;
          this.setState({
            currentLocation: {
              lat: coords.latitude,
              lng: coords.longitude
            }
          });
        });
      }
    }

    // CURRENT LOCATION NOT changed

    // console.log(this.state.currentLocation)

    this.loadMap();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.google !== this.props.google) {
      this.loadMap();
    }
    if (prevState.currentLocation !== this.state.currentLocation) {
      this.recenterMap();
    }
  }

  loadMap() {
    if (this.props && this.props.google) {
      // checks if google is available
      const { google } = this.props;
      const maps = google.maps;

      const mapRef = this.refs.map;

      // reference to the actual DOM element
      const node = ReactDOM.findDOMNode(mapRef);

      let { zoom } = this.props;
      let { lat, lng } = this.state.currentLocation;
    //   console.log(lat, lng);
      const center = new maps.LatLng(lat, lng);
      const mapConfig = Object.assign(
        {},
        {
          center: center,
          zoom: zoom
        }
      );
      // maps.Map() is constructor that instantiates the map
      this.map = new maps.Map(node, mapConfig);
      this.state.directionsDisplay.setMap(this.map);
      let destination = { //unit 1
        lat: 37.868112,
        lng: -122.255033
      }

    //   navigator.geolocation.getCurrentPosition(pos => {
    //     const coords = pos.coords;
    //     this.setState({
    //       currentLocation: {
    //         lat: coords.latitude,
    //         lng: coords.longitude
    //       }
    //     });
    //   });

    //   console.log(this.state);
    //   this.displayRoute
      this.displayRoute(destination);

    }
  }

  displayRoute(destination) { //display route from current location to specified destination\
    this.state.directionsService.route({
        origin: new this.props.google.maps.LatLng(this.state.currentLocation.lat, this.state.currentLocation.lng),
        destination: new this.props.google.maps.LatLng(destination.lat, destination.lng),
        travelMode: this.props.google.maps.TravelMode['WALKING']
        }, (response, status) => {
            if (status === 'OK') {
                console.log(this.state.currentLocation);
                this.state.directionsDisplay.setDirections(response);

                displayRouteModified(destination);
            } else {
                window.alert('Directions request failed due to: ' + status);
            }
        })
  }


  recenterMap() {
    const map = this.map;
    const current = this.state.currentLocation;

    const google = this.props.google;
    const maps = google.maps;

    if (map) {
      let center = new maps.LatLng(current.lat, current.lng);
      map.panTo(center);
    }
  }

  renderChildren() {
    const { children } = this.props;

    if (!children) return;

    return React.Children.map(children, c => {
      if (!c) return;
      return React.cloneElement(c, {
        map: this.map,
        google: this.props.google,
        mapCenter: this.state.currentLocation
      });
    });
  }

  render() {
    // console.log(this.props);
    const style = Object.assign({}, mapStyles.map);

    return (
      <div>
        <div style={style} ref="map">
          Loading map...
        </div>
        {this.renderChildren()}
      </div>
    );
  }
}
export default Map;

Map.defaultProps = {
  zoom: 14,
  initialCenter: {
    lat: 37.871295,
    lng: -122.260314
  },
  centerAroundCurrentLocation: true,
  visible: true
};
