class Signup extends Component {

    constructor(props) {
      super(props);
      this.state = {username: 'u', password: 'p'};

      this.handleChange = this.handleChange.bind(this);
      this.submitClicked= this.submitClicked.bind(this);
    }

    handleChange(event) {
      this.setState({username: event.target.value, password: event.target.value});
    }

    submitClicked = () => {
        alert('submit clicked!')
        console.log(this.state)
    }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />

            <label> Username:
            <input type="text" username={this.state.username} onChange={this.handleChange} />
          </label>

          <label> Password:
          <input type="text" password={this.state.password} onChange={this.handleChange} />
          </label>
          <br/>
          <input type="submit" username="Submit" onClick={this.submitClicked}/>

        </header>
      </div>
    );
  }
}

Signup.defaultProps = {
    value: '',
    username: "username",
    password: "password"
}
export default Signup
